import streamlit as st
import numpy as np
import joblib

# --- Custom CSS for background color ---
st.markdown(
    """
  <style>
  /* Background y'umweru no gushyira text y'umukara */
  body {
      background-color: white;
      color: black;
  }
  
  /* Style ya button ya Streamlit */
  .stButton>button {
      background-color: yellow;
      color: black;
      font-weight: bold;
  }

  /* Optional: center all content */
  .css-18e3th9 {
      text-align: center;
  }
  </style>
  """,
    
    unsafe_allow_html=True
)

# --- Load the trained model ---
MODEL_FILE = "flood_model_prediction.pkl"  # Your model file
model = joblib.load(MODEL_FILE)

# --- App title ---
st.title("Flood Prediction Dashboard Group1")
st.write("Enter Rainfall, River Level, Soil Moisture, Temperature, Humidity, and Elevation to get the model prediction.")

# --- Display flood-related image ---
st.image(
    "data:image/jpeg;base64,",
    caption="Flood Risk Visualization",
    use_column_width=True
)

# --- Input fields ---
col1, col2, col3 = st.columns(3)
Rainfall = col1.number_input("Rainfall (mm)", value=0.0, step=0.1)
River_Level = col2.number_input("River Level (m)", value=0.0, step=0.1)
Soil_Moisture = col3.number_input("Soil Moisture (%)", value=0.0, step=0.1)

col4, col5, col6 = st.columns(3)
Temperature = col4.number_input("Temperature (Â°C)", value=25.0, step=0.1)  # Added back Temperature
Humidity =col5.number_input("Humidity (%)", value=50.0, step=0.1)
Elevation = col6.number_input("Elevation (m)", value=0.0, step=0.1)

    
if st.button("Predict"):
    X = np.array([[Rainfall, River_Level, Soil_Moisture, Temperature, Humidity, Elevation]])
    prediction = model.predict(X)[0]
    st.success(f"Predicted Flood Status: **{prediction}**")